import React, { useState } from 'react';
import './Slider.css';
import { dataSlider } from './dataSlider';
  // Importez dataSlider de manière nommée
import BtnSlider from './BtnSlider';

export default function Slider() {
  const [index, setIndex] = useState(0);

  const nextSlide = () => 
  console.log('Next Slide Clicked');{
    if (index < dataSlider.length - 1) {
      setIndex(index + 1);
    } else {
      setIndex(0);
    }
  };

  const prevSlide = () => {
    if (index > 0) {
      setIndex(index - 1);
    } else {
      setIndex(dataSlider.length - 1);
    }
  };

  return (
    <div className="container-slider">
      {dataSlider.map((obj, slideIndex) => (
        <div key={obj.id} className={`slide ${slideIndex === index ? 'active' : ''}`}>
          <h1>{obj.title}</h1>
          <img src={process.env.PUBLIC_URL + `/Imgs/img${slideIndex + 1}.jpg`} alt="" />
        </div>
      ))}
      <BtnSlider moveSlide={nextSlide} direction="next" />
      <BtnSlider moveSlide={prevSlide} direction="prev" />
    </div>
  );
}
