import React, { useState } from 'react'
import Item from './Item'
import { v4 as uuidv4 } from 'uuid';


export default function Form() {

/***********Tableau des taches***************/

    const [dataArr,setDataArr]=useState([
        {txt:'Sport',id:uuidv4(),completed:false},
        {txt:'regarder Film',id:uuidv4(),completed:false},
        {txt:'Coder en React',id:uuidv4(),completed:false}
        ])

/***********input***************/
    const [valtxt,setvaltxt]=useState('');

const modifytext=(e)=>{

setvaltxt(e.target.value);
console.log(valtxt);

}
/********fonction addTodo**********************/
const addTodo = e => {        
    e.preventDefault(); 

 /***** ne jamais modifier le state directement******/     
    const newArr = [...dataArr]      
    const newTodo = {};       
    newTodo.txt = valtxt;    
    newTodo.id = uuidv4();       
    newArr.push(newTodo);    
    setDataArr(newArr);       
    setvaltxt('')    ;
}


/******fonction deleteelement******************/


const deleteelement=id=>{

    
 /***** ne jamais modifier le state directement******/ 
    let  newArr = [...dataArr]
    newArr=newArr.filter(element=>{
    return element.id!=id;
    })
 setDataArr(newArr);
    }

/******fonction toggleTodo******************/

    const toggleTodo = (id) => {
       // alert('hello'+id)
        const newArr = dataArr.map((item) => {
            if (item.id === id) {
                return {...item, completed: !item.completed };
            }
            return item;
        });
        setDataArr(newArr);
    }

    

    return (
    
    <div className="m-auto px-4 col-12 col-sm-10 col-lg-6">
<form onSubmit={e => addTodo(e)}  className="mb-3">
<label htmlFor="todo" className="form-label mt-3">Choses à faire</label>
<input type="text" className="form-control" id="todo" value={valtxt}    onChange={(e)=>modifytext(e)} />
<button className="mt-2 btn btn-primary d-block">Envoyer</button>
    </form>



    <ul>
     {
     dataArr.map(element => {
      return <Item completed={element.completed} txt={element.txt} key={element.id}  toggleTodo={() => toggleTodo(element.id)}  delFunc={()=>deleteelement(element.id)}  />
     })
     
     }
     </ul>

</div>
    );
    
}
